package com.autught.livepayclass PayResult(rawResult: Map<String, String>?) {    var resultStatus: String? = null        private set    var result: String? = null        private set    var memo: String? = null        private set    override fun toString(): String {        return ("resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}")    }    init {        rawResult?.forEach { entry ->            when (entry.key) {                "resultStatus" -> resultStatus = entry.value                "result" -> result = entry.value                "memo" -> memo = entry.value            }        }    }}